package onion.bread.botfights.config;

import lombok.RequiredArgsConstructor;
import onion.bread.botfights.controllers.MatchController;
import onion.bread.botfights.dao.MatchDao;
import onion.bread.botfights.dao.TeamDao;
import onion.bread.botfights.model.Match;
import onion.bread.botfights.model.MatchStatus;
import onion.bread.botfights.model.Team;
import org.springframework.context.event.ContextRefreshedEvent;
import org.springframework.context.event.EventListener;
import org.springframework.stereotype.Component;

import java.util.Arrays;
import java.util.List;

@Component
@RequiredArgsConstructor
public class DataInitListener {

    private final MatchDao matchDao;

    private final TeamDao teamDao;

    private final MatchController matchController;

    @EventListener(value = ContextRefreshedEvent.class)
    public void initData() {
        matchDao.saveAll(genMatches());
        teamDao.saveAll(genTeams());
        matchController.start();
    }

    private Iterable<Team> genTeams() {
        Team team1 = Team.builder().name("IVAN").players(Arrays.asList("hz", "kto", "tam")).fullName("IVAN")
                .logo("data:image/jpeg;base64,").build();
        Team team2 = Team.builder().name("KG").players(Arrays.asList("vse", "eshe", "hz")).fullName("Kind Geniuses")
                .logo("data:image/jpeg;base64,").build();
        Team team3 = Team.builder().name("KK").players(Arrays.asList("bot3", "bot2", "bot1")).fullName("Kony Kartinka")
                .logo("data:image/jpeg;base64,").build();
        Team team4 = Team.builder().name("VPRO").players(Arrays.asList("PROV", "VROP", "VPOR")).fullName("Puur Vpro")
                .logo("data:image/jpeg;base64,").build();
        return Arrays.asList(team1, team2, team3, team4);
    }

    private List<Match> genMatches() {
        Match match1 = Match.builder()
                .team1("IVAN")
                .team2("KG")
                .team3("KK")
                .team4("VPRO")
                .timestamp("0")
                .status(MatchStatus.COMPLETED)
                .tournament("MAJOR").build();
        Match match2 = Match.builder()
                .team1("IVAN")
                .team2("KG")
                .team3("KK")
                .team4("VPRO")
                .status(MatchStatus.RUNNING)
                .timestamp("1337")
                .tournament("MINOR").build();
        Match match3 = Match.builder()
                .team1("IVAN")
                .team2("KG")
                .team3("KK")
                .team4("VPRO")
                .status(MatchStatus.UPCOMING)
                .timestamp("1488")
                .tournament("MAJOR").build();
        Match match4 = Match.builder()
                .team1("IVAN")
                .team2("KG")
                .team3("KK")
                .team4("VPRO")
                .status(MatchStatus.COMPLETED)
                .timestamp("228")
                .tournament("MAJOR").build();
        return Arrays.asList(match1, match2, match3, match4);
    }

}
